mapper.insert:
insert into appointment_main(appointment_id, appointment_time, client_username, barber_shop_name, service_name)
values(#{appointmentId},#{appointmentTime},#{clientUsername},#{barberShopName},#{serviceName});
insert into appointment_client(appointment_id, appointment_time, client_username, barber_shop_name, service_name)
values(#{appointmentId},#{appointmentTime},#{clientUsername},#{barberShopName},#{serviceName});
insert into appointment_barber(appointment_id, appointment_time, client_username, barber_shop_name, service_name)
values(#{appointmentId},#{appointmentTime},#{clientUsername},#{barberShopName},#{serviceName});

mapper.delete:
update appointment
set is_done=true
where appointment_id=#{appointmentId};

update appointment
set is_canceled=true
where appointment_id=#{appointmentId};

update appointment
set is_deleted=true
where appointment_id=#{appointmentId};
